package JavaTutorials;

public class ifThenStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String day = "Sunday";
		int noDay = 7;
		
		if(day == "Sunday")
		{
			System.out.println("Today is Sunday");
		}
		
		if(noDay == 7)
		{
			System.out.println("Day no. 7");
		}

	}

}
