package JavaTutorials;

public class whileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int Count=0;
		int iCount = 25;
		while(Count<=10)
		{
			System.out.println("Count is " + Count);
			Count += 1;
		}
		
		while(iCount<25)
		{
			System.out.println("Count is " + iCount);
			Count += 1;
		}

	}

}
